static void parse_encoding(const char *enc, enum w_pdu_type *pdu)
{
  if (!enc) {
    return;
  }
  if (!strcmp(enc, "json")) {
    *pdu = is_json_compact;
    return;
  }
  if (!strcmp(enc, "bser")) {
    *pdu = is_bser;
    return;
  }
  w_log(W_LOG_ERR, "Invalid encoding '%s', use one of json or bser\n", enc);
  exit(EX_USAGE);
}

static const char *get_env_with_fallback(const char *name1,
    const char *name2, const char *fallback)
{
  const char *val;

  val = getenv(name1);
  if (!val || *val == 0) {
    val = getenv(name2);
  }
  if (!val || *val == 0) {
    val = fallback;
  }

  return val;
}

static const char *get_state_dir(void)
{
  if (!state_dir) {
    const char *state_dir_template;
    char *expanded_state_dir;
    struct stat statbuf;
    int stat_ret;

    state_dir_template = getenv("WATCHMAN_STATE_DIR");
    if (!state_dir_template) {
      state_dir_template = WATCHMAN_STATE_DIR;
    }

#ifdef _WIN32
    {
      DWORD ret;
      DWORD len = ExpandEnvironmentStrings(state_dir_template, NULL, 0);
      if (len == 0) {
        w_log(W_LOG_ERR, "ExpandEnvironmentStrings failed");
        exit(1);
      }

      expanded_state_dir = malloc(len);
      if (expanded_state_dir == NULL) {
        w_log(W_LOG_ERR, "out of memory");
        exit(1);
      }

      ret = ExpandEnvironmentStrings(
        state_dir_template,
        expanded_state_dir,
        len);

      if (ret == 0 || ret > len) {
        w_log(W_LOG_ERR, "ExpandEnvironmentStrings weirdness");
        exit(1);
      }
    }
#else
    {
      char *old_ifs;
      int we_flags = WRDE_UNDEF;
      wordexp_t p;
      memset(&p, 0, sizeof (p));

      // Prevent word expansion in wordexp
      old_ifs = getenv("IFS");
      if (setenv("IFS", "", 1) != 0) {
        w_log(W_LOG_ERR, "setenv failed");
        exit(1);
      }

      switch (wordexp(state_dir_template, &p, we_flags)) {
        case 0:
          break; // Success
        case WRDE_BADCHAR:
          w_log(W_LOG_ERR,
                "bad character in state directory template %s",
                state_dir_template);
          exit(1);
        case WRDE_BADVAL:
          w_log(W_LOG_ERR,
                "undefined variable in state directory template %s",
                state_dir_template);
          exit(1);
        case WRDE_CMDSUB:
          w_log(W_LOG_ERR, "command substitution not supported");
          exit(1);
        case WRDE_NOSPACE:
          w_log(W_LOG_ERR, "out of memory");
          exit(1);
        case WRDE_SYNTAX:
          w_log(W_LOG_ERR, "shell syntax error");
          exit(1);
        default:
          w_log(W_LOG_ERR, "unknown error in wordexp");
          exit(1);
      }

      if (old_ifs) {
        setenv("IFS", old_ifs, 1);
      } else {
        unsetenv("IFS");
      }

      if (p.we_wordc != 1) {
        w_log(W_LOG_ERR, "word expansion unexpectedly split path");
        exit(1);
      }

      expanded_state_dir = strdup(p.we_wordv[0]);
      if (expanded_state_dir == NULL) {
        w_log(W_LOG_ERR, "out of memory");
        exit(1);
      }

      wordfree(&p);
    }
#endif

    stat_ret = stat(expanded_state_dir, &statbuf);
    if (stat_ret == 0 && !S_ISDIR(statbuf.st_mode)) {
      w_log(W_LOG_ERR,
            "state directory %s is not a directory",
            expanded_state_dir);
      exit(1);
    }

    if (stat_ret == -1 && errno != ENOENT && errno != ENOTDIR) {
      w_log(W_LOG_ERR, "stat of %s failed: %s",
            expanded_state_dir,
            strerror(errno));
      exit(1);
    }

    if (stat_ret == -1) {
      // Make sure all elements in the state directory path exist.
      // Easier to shell out than iterate over the path elements,
      // especially on Windows with its myriad path syntaxes.
      char *mkdir_command = NULL;
      w_string_t *state_dir_s = w_string_new(expanded_state_dir);
      w_string_t *escaped_state_dir = w_string_shell_escape(state_dir_s);
      ignore_result(
        asprintf(&mkdir_command,
#ifdef _WIN32
                 "mkdir %s",
#else
                 "mkdir -p %s",
#endif
                 escaped_state_dir->buf));

      w_string_delref(state_dir_s);
      w_string_delref(escaped_state_dir);

      if (mkdir_command == NULL) {
        w_log(W_LOG_ERR, "creating mkdir command failed");
        exit(1);
      }

      if (system(mkdir_command) != 0) {
        w_log(W_LOG_ERR, "mkdir command \"%s\" failed", mkdir_command);
        exit(1);
      }

      free(mkdir_command);
    }

    state_dir = expanded_state_dir;
  }

  return state_dir;
}

static void compute_file_name(char **strp,
    const char *suffix,
    const char *what)
{
  char *str = NULL;

  str = *strp;

  if (!str) {
    ignore_result(asprintf(&str, "%s%cwatchman%s%s",
                           get_state_dir(),
                           WATCHMAN_DIR_SEP,
                           suffix[0] ? "." : "",
                           suffix));
  }

  if (!str) {
    w_log(W_LOG_ERR, "out of memory computing %s", what);
    abort();
  }

#ifndef _WIN32
  if (str[0] != '/') {
    w_log(W_LOG_ERR, "invalid %s: %s", what, str);
    exit(1);
  }
#endif

  *strp = str;
}

static void setup_sock_name(void)
{
  watchman_tmp_dir = get_env_with_fallback("TMPDIR", "TMP", "/tmp");

#ifdef _WIN32
  if (!sock_name) {
    char* username = getenv("USERNAME");
    if (!username) {
      w_log(W_LOG_ERR, "USERNAME not set in environment");
      abort();
    }

    asprintf(&sock_name, "\\\\.\\pipe\\watchman-%s", username);
  }
#else
  compute_file_name(&sock_name, "", "sockname");
#endif
  compute_file_name(&watchman_state_file, "state", "statefile");
  compute_file_name(&log_name, "log", "logname");
#ifdef USE_GIMLI
  compute_file_name(&pid_file, "pid", "pidfile");
#endif

#ifndef _WIN32
  un.sun_family = PF_LOCAL;
  strcpy(un.sun_path, sock_name);

  if (strlen(sock_name) >= sizeof(un.sun_path) - 1) {
    w_log(W_LOG_ERR, "%s: path is too long\n",
        sock_name);
    abort();
  }
#endif
}

static bool should_start(int err)
{
  if (err == ECONNREFUSED) {
    return true;
  }
  if (err == ENOENT) {
    return true;
  }
  return false;
}

static bool try_command(json_t *cmd, int timeout)
{
  w_stm_t client = NULL;
  w_jbuffer_t buffer;
  w_jbuffer_t output_pdu_buffer;
  int err;

  client = w_stm_connect(sock_name, timeout * 1000);
  if (client == NULL) {
    return false;
  }

  if (!cmd) {
    w_stm_close(client);
    return true;
  }

  w_json_buffer_init(&buffer);

  // Send command
  if (!w_ser_write_pdu(server_pdu, &buffer, client, cmd)) {
    err = errno;
    w_log(W_LOG_ERR, "error sending PDU to server\n");
    w_json_buffer_free(&buffer);
    w_stm_close(client);
    errno = err;
    return false;
  }

  w_json_buffer_reset(&buffer);

  w_json_buffer_init(&output_pdu_buffer);

  do {
    if (!w_json_buffer_passthru(
          &buffer, output_pdu, &output_pdu_buffer, client)) {
      err = errno;
      w_json_buffer_free(&buffer);
      w_json_buffer_free(&output_pdu_buffer);
      w_stm_close(client);
      errno = err;
      return false;
    }
  } while (persistent);
  w_json_buffer_free(&buffer);
  w_json_buffer_free(&output_pdu_buffer);
  w_stm_close(client);

  return true;
}

static struct watchman_getopt opts[] = {
  { "help",     'h', "Show this help",
    OPT_NONE,   &show_help, NULL, NOT_DAEMON },
  { "version",  'v', "Show version number",
    OPT_NONE,   &show_version, NULL, NOT_DAEMON },
  { "sockname", 'U', "Specify alternate sockname",
    REQ_STRING, &sock_name, "PATH", IS_DAEMON },
  { "logfile", 'o', "Specify path to logfile",
    REQ_STRING, &log_name, "PATH", IS_DAEMON },
  { "log-level", 0, "set the log level (0 = off, default is 1, verbose = 2)",
    REQ_INT, &log_level, NULL, IS_DAEMON },
#ifdef USE_GIMLI
  { "pidfile", 0, "Specify path to gimli monitor pidfile",
    REQ_STRING, &pid_file, "PATH", NOT_DAEMON },
#endif
  { "persistent", 'p', "Persist and wait for further responses",
    OPT_NONE, &persistent, NULL, NOT_DAEMON },
  { "no-save-state", 'n', "Don't save state between invocations",
    OPT_NONE, &dont_save_state, NULL, IS_DAEMON },
  { "statefile", 0, "Specify path to file to hold watch and trigger state",
    REQ_STRING, &watchman_state_file, "PATH", IS_DAEMON },
  { "json-command", 'j', "Instead of parsing CLI arguments, take a single "
    "json object from stdin",
    OPT_NONE, &json_input_arg, NULL, NOT_DAEMON },
  { "output-encoding", 0, "CLI output encoding. json (default) or bser",
    REQ_STRING, &output_encoding, NULL, NOT_DAEMON },
  { "server-encoding", 0, "CLI<->server encoding. bser (default) or json",
    REQ_STRING, &server_encoding, NULL, NOT_DAEMON },
  { "foreground", 'f', "Run the service in the foreground",
    OPT_NONE, &foreground, NULL, NOT_DAEMON },
  { "no-pretty", 0, "Don't pretty print JSON",
    OPT_NONE, &no_pretty, NULL, NOT_DAEMON },
  { "no-spawn", 0, "Don't try to start the service if it is not available",
    OPT_NONE, &no_spawn, NULL, NOT_DAEMON },
  { "no-local", 0, "When no-spawn is enabled, don't try to handle request"
    " in client mode if service is unavailable",
    OPT_NONE, &no_local, NULL, NOT_DAEMON },
  { 0, 0, 0, 0, 0, 0, 0 }
};

static void parse_cmdline(int *argcp, char ***argvp)
{
  cfg_load_global_config_file();
  w_getopt(opts, argcp, argvp, &daemon_argv);
  if (show_help) {
    usage(opts, stdout);
  }
  if (show_version) {
    printf("%s\n", PACKAGE_VERSION);
    exit(0);
  }
  setup_sock_name();
  parse_encoding(server_encoding, &server_pdu);
  parse_encoding(output_encoding, &output_pdu);
  if (!output_encoding) {
    output_pdu = no_pretty ? is_json_compact : is_json_pretty;
  }
}

static json_t *build_command(int argc, char **argv)
{
  json_t *cmd;
  int i;

  // Read blob from stdin
  if (json_input_arg) {
    json_error_t err;
    w_jbuffer_t buf;

    memset(&err, 0, sizeof(err));
    w_json_buffer_init(&buf);
    cmd = w_json_buffer_next(&buf, w_stm_stdin(), &err);

    if (buf.pdu_type == is_bser) {
      // If they used bser for the input, select bser for output
      // unless they explicitly requested something else
      if (!server_encoding) {
        server_pdu = is_bser;
      }
      if (!output_encoding) {
        output_pdu = is_bser;
      }
    }

    w_json_buffer_free(&buf);

    if (cmd == NULL) {
      fprintf(stderr, "failed to parse command from stdin: %s\n",
          err.text);
      exit(1);
    }
    return cmd;
  }

  // Special case: no arguments means that we just want
  // to verify that the service is up, starting it if
  // needed
  if (argc == 0) {
    return NULL;
  }

  cmd = json_array();
  for (i = 0; i < argc; i++) {
    json_array_append_new(cmd, json_string(argv[i]));
  }

  return cmd;
}

const char *get_sock_name(void)
{
  return sock_name;
}

int main(int argc, char **argv)
{
  bool ran;
  json_t *cmd;

  w_client_lock_init();
  parse_cmdline(&argc, &argv);

  if (foreground) {
    run_service();
    return 0;
  }

  w_set_thread_name("cli");
  cmd = build_command(argc, argv);
  preprocess_command(cmd, output_pdu);

  ran = try_command(cmd, 0);
  if (!ran && should_start(errno)) {
    if (no_spawn) {
      if (!no_local) {
        ran = try_client_mode_command(cmd, !no_pretty);
      }
    } else {
#ifdef USE_GIMLI
      spawn_via_gimli();
#elif defined(__APPLE__)
      spawn_via_launchd();
#elif defined(_WIN32)
      spawn_win32();
#else
      daemonize();
#endif
      ran = try_command(cmd, 10);
    }
  }

  json_decref(cmd);

  if (ran) {
    return 0;
  }

  if (!no_spawn) {
    w_log(W_LOG_ERR, "unable to talk to your watchman on %s! (%s)\n",
        sock_name, strerror(errno));
#ifdef __APPLE__
    if (getenv("TMUX")) {
      w_log(W_LOG_ERR, "\n"
"You may be hitting a tmux related session issue.\n"
"An immediate workaround is to run:\n"
"\n"
"    watchman version\n"
"\n"
"just once, from *outside* your tmux session, to allow the launchd\n"
"registration to be setup.  Once done, you can continue to access\n"
"watchman from inside your tmux sessions as usual.\n"
"\n"
"Longer term, you may wish to install this tool:\n"
"\n"
"    https://github.com/ChrisJohnsen/tmux-MacOSX-pasteboard\n"
"\n"
"and configure tmux to use `reattach-to-user-namespace`\n"
"when it launches your shell.\n");
    }
#endif
  }
  return 1;
}

