ssize_t stringlen(char *instr)
{
	ssize_t counter = 0;
	pr_info("sringlen is called.\n");

	while(instr[counter] != '\0')
	{
		pr_info("%c %d\n",instr[counter],counter);
		counter++;
	}
	return counter;
}
