{
	struct this_dev_data *this_device = platform_get_drvdata(pdev);
	pr_info("my_remove() function is called.\n");
	//misc_deregister(&helloworld_miscdevice);
	misc_deregister(&this_device->hellokeydev);
	return 0;
}

